import { useState, useReducer } from "react"
import Button from '../components/Button'
import Field from '../components/Field'
import Input from '../components/Input'
import Label from '../components/Label'
import EmailService from '../services/email'

function reducer(state, action){
    return state;
}

export default props => {
    const [isSignUp, setIsSignUp] = useState(false );

    //{name: String, password: String, email: String, error: {name, password, email}}

    const [state, dispatch] = useReducer(reducer);
    return <>
        <div className="page">
            <div className="form">
                {isSignUp && (
                    <>
                        <Field>
                            <Label> Name</Label>
                            <Input name="name" type="text" onChange= {({target})=> dispatch({type: 'update-name', value: target.value})}/>
                        </Field>
                    </>
                )}
                <Field>
                    <Label> Email</Label>
                    <Input type="email" />
                </Field>
                <Field>
                    <Label> Password</Label>
                    <Input type="password" />
                </Field>
                <Button>{isSignUp ? "Sign up" : "Log in"}</Button>
                <p className="log-in-prompt">
                    {isSignUp ? "Have" : "Need"} an account?
                            <span className="small">
                        Click below, fill out the form, et voila!
                            </span>
                </p>
                <Button onClick={() => setIsSignUp(!isSignUp)}>
                    {isSignUp ? "Log in" : "Sign up"}
                </Button>
                <p className="error">{"errors?.auth"} </p>
            </div>
        </div>
        <style jsx>{`
        .error {
          text-align: center;
          color: var(--error);
        }
        .page {
          width: 100%;
          min-height: 100vh;
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
        }
        .log-in-prompt {
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
          margin-top: 0.25rem;
        }
        .log-in-prompt > .small {
          font-size: 0.75rem;
          opacity: 0.6;
        }
        .form {
          display: flex;
          flex-direction: column;
          width: 300px;
        }
        .form > :global(.field),
        .form > :global(.btn) {
          margin-bottom: 0.5rem;
        }
      `}</style>


    </>
}
