import { useState } from "react";
import { Formik, Form } from "formik";
import fetch from "isomorphic-unfetch";
import * as Yup from "yup";
import Router from "next/router";
import Cookie from "js-cookie";
import Input from "../components/Input";
import Field from "../components/Field";
import Label from "../components/Label";
import Button from "../components/Button";
import useAuth from "../hooks/useAuth";
import AuthService from "../services/auth";
import Link from 'next/link'

import { Container, Row, Col, Card, CardBody, CardTitle, CardText } from 'reactstrap';


export default (props) => {
    const user = useAuth(props.user);

    return (
        <>

            <div className="login-link fixed-top px-3 text-left" style={{margin: 10,paddingTop: 20, paddingBottom:20, borderBottom: "1px solid #d7d7d7" }}>
                <Link href="/"><a>Home</a></Link>
                <Link href="/about"><a>About</a></Link>
                <Link href="/contact"><a>Contact</a></Link>
                    <p style={{display: "inline", color: "#000000"}}>{user.name}</p>
            </div>
            <Card className="mb-2 col-xs-8 col-lg-6 offset-lg-1" style={{ borderColor: "#6bc3b9", border: "1px solid #6bc3b9", paddingTop: 20, paddingBottom: 20, backgroundColor: "#ffffff" }}>
                <CardBody>

                </CardBody>
            </Card>
            <style jsx>{`
        .login-link {
          font-size: 20px; !important
          position: relative;
          top: 8px;
          left: 10px;
          font-weight: normal;
          font-family: "Times New Roman", Times, serif;
        }

      `}</style>
        </>
    )
}

export async function getServerSideProps(context) {
    return {
        props: {
            user: await AuthService.getUserFromCookie(context.req),
        },
    };
}
