import { useState, useReducer } from "react"
import Button from '../components/Button'
import Field from '../components/Field'
import Input from '../components/Input'
import Label from '../components/Label'
import EmailService from '../services/email'

function reducer(state, action) {
    switch (action.type) {
        case 'update-name':
            state.name = action.value;
            const error = action.value < 2 ? 'not valid' :undefined;
            // if (action.value < 2) {
            //     if (state.errors === undefined) {
            //         state.errors = {}
            //     }
            //     state.errors.name = "not valid"
            // } else {
            //     if(state.errors){
            //         state.errors.name = ""
            //     }
            // }
             return {...state, name: action.value, errors: {...state.errors, name: error}};
        default:
            return state;
    }
    return state;
}

export default props => {
    const [isSignUp, setIsSignUp] = useState(false);

    //{name: String, password: String, email: String, error: {name, password, email}}

    const [state, dispatch] = useReducer(reducer, {});
    return <>
        <div className="page">
            <div className="form">
                {isSignUp && (
                    <>
                        <Field>
                            <Label error={state.errors?.name}> Name</Label>
                            <Input name="name" type="text" value={state.name} onChange={({ target }) => dispatch({ type: 'update-name', value: target.value })} />
                        </Field>
                    </>
                )}
                <Field>
                    <Label> Email</Label>
                    <Input name="email" type="email" onChange={({ target }) => dispatch({ type: 'update-email', value: target.value })} />
                </Field>
                <Field>
                    <Label> Password</Label>
                    <Input name="password" type="password" onChange={({ target }) => dispatch({ type: 'update-password', value: target.value })} />
                </Field>
                <Button>{isSignUp ? "Sign up" : "Log in"}</Button>
                <p className="log-in-prompt">
                    {isSignUp ? "Have" : "Need"} an account?
                            <span className="small">
                        Click below, fill out the form, et voila!
                            </span>
                </p>
                <Button onClick={() => setIsSignUp(!isSignUp)}>
                    {isSignUp ? "Log in" : "Sign up"}
                </Button>
                <p className="error">{"errors?.auth"} </p>
            </div>
        </div>
        <style jsx>{`
        .error {
          text-align: center;
          color: var(--error);
        }
        .page {
          width: 100%;
          min-height: 100vh;
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
        }
        .log-in-prompt {
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
          margin-top: 0.25rem;
        }
        .log-in-prompt > .small {
          font-size: 0.75rem;
          opacity: 0.6;
        }
        .form {
          display: flex;
          flex-direction: column;
          width: 300px;
        }
        .form > :global(.field),
        .form > :global(.btn) {
          margin-bottom: 0.5rem;
        }
      `}</style>


    </>
}
