import React, { Component } from 'react'
import dynamic from 'next/dynamic'
import Link from 'next/link'
import 'bootstrap/dist/css/bootstrap.min.css';
import useAuth from "../hooks/useAuth";
import AuthService from "../services/auth";


import window from 'global'

const mySpecialWindowFunction = () => {
    return /msie [6-9]\b/.test(window.navigator.userAgent.toLowerCase());
};
const DynamicVideoComponent = dynamic(import('../components/videoAnnotationDemo/VideoAnnotationDemo.js'), {
  ssr: false
})

export default (props) => {
  const user = useAuth(props.user);
  console.log("User is:"+JSON.stringify(user))
  return (
    <>
      {/* <Link href="/login"><button>Login</button>
        </Link> */}

      {/* <div className="fixed-top px-3 text-right">
        <a href="https://www.npmjs.com/package/react-annotation-tool">
          <img src="https://img.shields.io/npm/v/react-annotation-tool.svg?branch=master" className="img-fluid" alt="Responsive image" />
        </a>
      </div> */}
      <div className="login-link  px-3 text-left" style={{backgroundColor: "#fffff0", paddingTop: 20, paddingBottom:20, borderBottom: "1px solid #d7d7d7"}}>
          <Link href="/"><a style={{color: "#000000", fontFamily: "Playfair Display, serif"}}>Home</a></Link>
           <Link href="/about"><a style={{fontFamily: "Playfair Display, serif"}}>About</a></Link>
           <Link href="/contact"><a style={{ fontFamily: "Playfair Display, serif"}}>Contact</a></Link>
           {user._id
          ? <Link href="/login" className='loginClass' style={{display: "inline", color: "#007bff",  fontFamily: "Playfair Display, serif"}}>{user.name}</Link>
          : <Link href="/login" className='loginClass'><a style={{ fontFamily: "Playfair Display, serif"}}>Login</a>
             </Link>}
        {user._id
          ? <Link href="/profile" ><a>Profile</a>
          </Link>
          : ``}
      </div>


      {/* <hr /> */}
      <DynamicVideoComponent />
      <style jsx>{`
        .login-link {
          font-size: 20px; !important
          position: fixed !important;
          top: 8px;
          left: 10px;
          font-weight: normal;
          font-family: "Times New Roman", Times, serif;
        }
        .loginClass{
          margin-left: 50px;
        }

      `}</style>
    </>
  );
};

// NODE JS CODE
export async function getServerSideProps(context) {

  return {
    props: {
      user: await AuthService.getUserFromCookie(context.req),
    },
  };
}
